import React from "react";
import "./Hero.css";
import myImage from "../assets/logo.png";

function Hero() {
  return (
    <div className="hero__main">
      <div className="hero__top">
        <img src={myImage} alt="check source" />
        <button>Sign in</button>
      </div>

      <div className='hero__bottom'>
        h3
      </div>
    </div>
  );
}

export default Hero;
